CREATE TABLE tb_post (
    id integer NOT NULL,
    titulo character varying(70) NOT NULL,
    tags character varying(25)[] NOT NULL,
    texto text NOT NULL
);


CREATE SEQUENCE tb_post_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1;



ALTER TABLE ONLY tb_post ALTER COLUMN id SET DEFAULT nextval('tb_post_id_seq'::regclass);




SELECT pg_catalog.setval('tb_post_id_seq', 20, true);


ALTER TABLE ONLY tb_post
    ADD CONSTRAINT tb_post_pkey PRIMARY KEY (id);

ALTER TABLE tb_post CLUSTER ON tb_post_pkey;
